glsl1.frag:
precision highp float;
#if USE_ALPHA_TEST
      uniform float alphaThreshold;
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
    if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
    if (alpha < alphaThreshold) discard;
  #endif
}
uniform highp vec4 cc_time;
varying vec4 v_color;
#if USE_TEXTURE
  varying vec2 v_uv0;
  uniform sampler2D cc_spriteTexture;
#endif
float circle(in vec2 uv, in float radius){
  return 1.0 - smoothstep(radius - 0.01 * radius, radius + 0.01 * radius, length(uv));
}
vec4 frag () {
  vec4 o = vec4(0, 0, 0, 0);
  #if USE_TEXTURE
    vec2 uv = v_uv0 - 0.5;
    float R = 0.35;
    float radius = 0.05;
    vec2 pos = vec2(0.0);
    float rotation = 0.0;
    const float count = 12.0;
    float time = cc_time.x;
    for (float i = 0.0; i < count; i++) {
      rotation = 2.0 * 3.141592653 * i / count + time * 2.0 * 3.141592653 / 4.0;
      pos = vec2(R * cos(rotation), R * sin(rotation));
      o.a += circle(uv - pos, radius * (sin(rotation * 0.45) + 1.0) / 3.0 + radius / 3.0);
      o.r += o.a * (sin(i / count + time + 3.141592653 * 6.0 / 3.0) + 1.0) / 2.0;
      o.g += o.a * (sin(i / count + time + 3.141592653 * 4.0 / 3.0) + 1.0) / 2.0;
      o.b += o.a * (sin(i / count + time + 3.141592653 * 2.0 / 3.0) + 1.0) / 2.0;
    }
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  return o;
}
void main() { gl_FragColor = frag(); }
